package ifElse;

public class Main {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
        int num=15;
		
		/*
		 * Note:
		 * In brackets is boolean (true or false)
		*/
			 
		if(num < 10) { // if
			// Instructions
			System.out.println("El número es menor a 10");
		}
		else { // Case other
			// Instructions
			System.out.println("El número no es menor a 10");
		}

	}

}
